INSERT INTO COMPANIES (name, description, location, website, certificateId) VALUES ('Walgreens Boots Alliance', 'Walgreens Boots Alliance, Inc. is an American holding company headquartered in Deerfield, Illinois that owns Walgreens, Boots and a number of pharmaceutical manufacturing, wholesale and distribution companies. The company was formed on December 31, 2014 after Walgreens purchased the 55 stake in Switzerlandbased Alliance Boots that it did not already own. Walgreens had previously purchased 45 of the company in 2012 with an option to purchase the remaining shares within three years.', 'Deerfield, Illinois, Stefano Pessina', 'http://walgreensbootsalliance.com', 47);
